import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Assigment3';

  holder = false;
  counter = 0;

  onClick(){

    this.holder = true;
    this.counter = this.counter+ 1;

  }

}
<button (click)="holder = !holder"> Display Details</button>

<p *ngIf="holder">Secret password : tuna</p>
<p *ngClass=""> {{counter}}</p>
